model = api.load('word2vec-ruscorpora-300')

morph = pymorphy2.MorphAnalyzer()

tag_fix = {'ADJF': 'ADJ', 'ADJS': 'ADJ', 'INFN': 'VERB', 'PRED': 'ADV', 'ADVB': 'ADV', 'CONJ' : 'SCONJ'}


def pp(sentence):
    for c in punc:
        sentence = sentence.replace(c, ' ')
    result = []
    for w in sentence.lower().split():
        if w not in russian_stopwords:
            p = morph.parse(w)[0]
            # print(w)
            nf = p.normal_form
            pos = p.tag.POS if p.tag.POS is not None else ''
            if pos in set(tag_fix.keys()):
                pos = tag_fix[pos]
            result.append(nf + '_' + pos)
    return result


def extract_keywords(text):
    return [w[0] for w in Counter(pp(text)).most_common(10)]


class Article:
    def __init__(self, header, subheader, body, link, tags=''):
        self.header = header
        self.subheader = subheader
        self.body = body
        self.link = link
        self.tags = tags

        self.pheader = pp(header)
        self.psubheader = pp(subheader)
        self.pbody = extract_keywords(body)
        self.ptags = pp(tags)

        self.characteristic = compile(self.pheader, self.psubheader, self.pbody, self.ptags)


def compile(pheader, psubheader, pbody, ptags):
    result = []
    result += pheader
    result += psubheader
    result += ptags
    result += pbody
    return result


p_sch = set(pp("ФРКТ ЛФИ ФАКТ ФЭФМ ФПМИ ФБМФ ИНБИКСТ"))

