def newdist(a, b):
    return model.wmdistance(a, b) / (1 + 0.1 * len(set(a) & set(b) & p_sch))


def getmetric(question, article):
    return newdist(pp(question), article.characteristic)


def head_join(header, list):
    return [header + ' ' + el for el in list]


def make_sub_headers(marked):
    marked_plus = []
    curh = ''
    cur = []
    for i in marked:
        if i[0] == '*':
            marked_plus += head_join(curh[1:], cur)
            cur = []
            curh = i
            marked_plus.append(i[1:])
        else:
            cur.append(i)
    marked_plus += head_join(curh[1:], cur)
    return marked_plus


def custlink(i):
    return '#{}'.format(i)


def create_articles(header, marked, body, main_link, tags = ''):
    ars = []
    sub_headers = make_sub_headers(marked);
    for i, sh in enumerate(sub_headers):
        a = Article(header, sh, body, main_link + custlink(i + 1), tags)
        ars.append(a)
    return ars

